insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('4.april','Voždovac','Vojvode Stepe','320','Sačinjavaju ga A blok sa zasebnim kupatilima i B blok sa spratnim kupatilima.
