insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Karaburma','Palilula','Mije Kovačevića','7b','Obe kule broje 690 studentskih soba. Zajednička ( spratna) kupatila zadovoljavaju visoke standarde.
		U čitaonicu studentskog doma "KARABURMA" može se ući isključivo uz pomoć studentske kartice.
		Studentima su na raspolaganju sala za tribine i predstave, teretana, klub, pošta,
		ambulanta Studentske poliklinike, nekoliko komercijalnih objekata, igralište i studentski restoran.
		Svake godine, početkom septembra, brucoši iz zemlje i okruženja predaju dokumenta  po konkursu za 
		prijem u dom u Maloj sali studentskog doma',null,null,102);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id)  values('Kralj Aleksandar I','Zvezdara','Bulevar Kralja Aleksandra I','75','Svojom spoljašnjom autentičnošću i luksuzno uređenim enterijerom, jedan je od najlepših i najsavremenijih studentskih domova.
		Moderno je opremljen i broji 195 studentskih soba. Dom ima video nadzor i internet u svim sobama.
		Impresivan hol i hodnici svedoče o otmenosti građanske Srbije između dva rata, a sobe pružaju studentima-stanarima osećaj sigurnosti i udobnosti.
		Lociran u samom centru Beograda, omogućava svojim stanarima da brzo stignu do svojih fakulteta, ali i do svih gradskih sadržaja, zanimljivih za studente.
		U okviru doma nalazi se i studentski restoran.',null,null,103);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Košutnjak','Čukarica','Blagoja Parovića','156','Studentske sobe kojih ima 123, raspoređene su u A i B bloku.
		U blizini doma nalaze se Fakultet za sport i fizičko vaspitanje, Šumarski fakulteti i Visoka hotelijerska škola.
		Specifičnost doma čini atraktivno okruženje, blizina park šume Košutnjak, otvorenih bazena i veštačke ski staze.
		Dom raspolaže elektronskim bravama, a studenti su u mogućnosti da pale ili gase svetlo u sobama pomoću studentske kartice.',null,null,104);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Mika Mitrović','Voždovac','Kralja Vladimira','33','Potpuno je renoviran u skladu sa potrebama studenata-stanara i zahtevima studentskog standarda.
		Apartmani u prizemlju kojih ima 12,u potpunosti su prilagođeni studentima sa invaliditetom. Smeštaj je obezbeđen i njihovim personalnim asistentima.
		Renoviran je i studentski restoran u okviru doma. Ostatak kapaciteta koji podrazumeva 67 soba na spratu, rezervisan je za sve ostale studente koji su 
		kunkurisali za smeštaj u ovom domu.
		U svim sobama postoji priključak za internet i kablovski TV sistem. Smešten u mirnom delu Voždovca, ovaj dom nudi izvanredne uslove za učenje i odmor 
		od gradske vreve.',null,null,105);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Patris Lumumba','Zvezdara','Ljubice Lukovića','1','U okviru ovog doma nalaze se studentski restoran, terena za sportske aktivnosti i ostale prostorijame neophodne za učenje i društveni život studenata.
		Sa renoviranjem II bloka, broj studentskih soba popeo se na 443.
		Devojke su useljene u IV, V i VI blok, a mladići u I i III, dok je II blok mešovit.
		Specifičnost doma čini to što svake godine primi najviše brucoša, kao i položaj koji omogućava studentima da lako stignu do svojih fakulteta.',null,null,106);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Rifat Burdžević','Zvezdara','Milana Rakića','77','U sobama, pored uobičajenog nameštaja , postoji priključak za internet.
		Studentima su na raspolaganju čitaonica, klub i prodavnica komercijalnog tipa. Specifičnost doma čini prijatna atmosfera, blizina Zvezdarske šume, SC "Olimp".
		Deo kapaciteta rezervisan je za strane studente.
		Video nadzor, kao u svim ostalim domovima, na zavidnom je nivou.
		Prema anketama koje se vrše među studentima, studentski restoran "Rifat" ocenjen je kao najbolji među studentskim restoranima u Beogradu.',null,null,107);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Studentski grad','Novi Beograd','Tošin Bunar','143-151','U okviru svakog doma nalaze se portirnica i video nadzor. Svaka soba ima priključak za internet.
		- Dom I sadrži 440 studentskih soba, hol sa recepcijom, ambulantu Studentske poliklinike, hostelski deo.
		- U Domu II studenti se mogu smestiti u 521 sobu.
		Osim soba i čitaonica, u ovom domu nalazi se kancelarije Službe za informacione tehnologije, fotokopirnica, 
			frizerski salon, prostorije RTV STUDENTSKI GRAD, Foto klub, književni klub "Branko Miljković", 
			guslarsko društvo, sportsko društvo "Studentski grad". U oba doma, od 2011. godine, smešteni su strani studenti, 
			stipendisti Vlade Republike Srbije koji studiraju na fakultetima Univerziteta u Beogradu.
		- Dom III broji 512 studentskih soba a u njemu su smeštene prostorije AKUD-a "Španac" i pošta.
		- Dom IV raspolaže sa 504 studentske sobe. U njemu se nalazi i Paraklis sv. Jovana Zlatoustog.
 
		Kroz ovaj kompleks prošlo je na hiljade studenta, ispričano nebrojeno priča, sačuvano mnogo dragih uspomena.
		O Studenjaku su bivši stanari govrili, pisali, snimali filmove, u njemu određivali svoje živote, sticali ljubavi i prijateljstva. Sećaju ga se sa osmehom.
		Autentičan i još uvek najveći na Balkanu, Studentski grad je do danas zadržao tradiciju omiljene studentske kuće.',null,null,108);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Slobodan Penezić','Zvezdara','Bana Ivaniša','bb','Dom broji 378 studentskih soba. Idealan je za učenje i odmor jer je zaštićen od gradske buke.
		U svakoj sobi postoji priključak za internet .
		U prvom paviljonu smeštene su studentkinje, dok preostala dva paviljona pripadaju muškim stanarima.
		Dom nema svoj restoran, pa se njegovi stanari najčešće hrane u restoranu susednog doma „Rifat Burdžević” koji je udaljen oko 200 metara.
		Zbog blizine Zvezdarske šume i Olimpa, stanari imaju zavidne uslove za sport i rekreaciju.',null,null,109);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Vera Blagojević 1','Palilula','27.marta','48','Studentski dom Vera Blagojević I ili popularno Vera I, bio je prvi ženski studentski dom. Izgrađen 1936. godine, 
