insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Vera Blagojević 2','Palilula','Dalmatinska','37','Studentski dom Vera Blagojević II ili popularno Vera II, bio je prvi ženski studentski dom. Izgrađen 1936. godine, 
