insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Žarko Marinović','Zemun','Cara Dušana','254','Studentski dom "Žarko Marinović" predstavlja najmanji objekat Ustanove studentski centar "Beograd".
