insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Ekonomski fakultet','Savski venac','Kamenička','6','Ekonomski fakultet Univerziteta u Beogradu je obrazovna i naučna ustanova sa tradicijom dugom skoro 80 godina. 
			Osnovan je kao Visoka škola za ekonomiju i trgovinu još daleke 1937. godine, kao vodeći visokoškolski obrazovni 
			centar u oblasti ekonomije u tadašnjoj Kraljevini Jugoslaviji.
			Danas je Ekonomski fakultet najveća i najeminentnija naučno-obrazovna ustanova u regionu u polju ekonomskih nauka, 
			poslovnog upravljanja i statistike. Fakultet zapošljava oko 130 nastavnika i saradnika od čega 55 redovnih Profesora. 
			Na Fakultetu je do sada diplomiralo preko 46000 studenata, preko 3500 je nastavilo poslediplomske studije i steklo
			naziv magistra nauka ili mastera dok je oko 760 steklo titulu doktora nauka.',301,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Pravni fakultet','Palilula','Bulevar kralja Aleksandra','67','Pravni fakultet Univerziteta u Beogradu, kao deo jednog od najstarijih univerziteta na Balkanu, sa temeljima u prvoj
			polovini 19. veka, bio je uvek vodeća srpska i jugoslovenska obrazovna i naučna institucija u oblasti prava. Okupljao je 
			vrhunske naučnike i vrsne predavače koji su obrazovali oko 50.000 diplomiranih pravnika i preko 2.000 magistara i doktora
			nauka. Studije ovog Fakulteta predstavljale su rasadnik najprestižnijih stručnjaka, naučnika i nastavnika za celu bivšu 
			Jugoslaviju.',302,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Pravoslavni bogoslovski fakultet','Palilula','Mije Kovačevića','11b','Danas na Pravoslavnom bogoslovskom fakultetu studira više od 1.800 mladića i devojaka koji se osposobljavaju za sveštenički,
			veroučiteljski poziv i naučni rad u oblasti teologije. Na Fakultetu predaje 42 nastavnika i saradnika. U predavačkom sastavu
			zastupljeni su i ugledni arhijereji naše Crkve, sveštenici, monasi.',303,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Učiteljski fakultet','Stari grad','Kraljice Natalije','43','Obrazovanje učitelja u Srbiji ima dugu tradiciju i ono traje punih 180 godina. Pamte se tri datuma u razvoju obrazovanja 
			učitelja: januara 1871. godine počinje sa radom Učiteljska škola u Srbiji, sa sedištem u Kragujevcu. 1878. godine ona je preseljena 
			u Beograd. 1812. godine osnovana je Prva srpska Učiteljska škola u Sent Andreji, koja je 1816. godine preseljena u Sombor. Učiteljska 
			škola u Beogradu deluje od 1878. godine, sa prekidima u ratnim godinama, sve do septembra 1972. godine. Septembra meseca 1972. godine 
			započela je sa radom Pedagoška akademija u Beogradu, a za proteklih dvadeset godina u njoj je diplomiralo hiljadu nastavnika razredne 
			nastave.',304,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fakultet bezbednosti','Voždovac','Gospodara Vučića','50','Fakultet bezbednosti je visokoškolska obrazovno-naučna institucija u sastavu Univerziteta u Beogradu i pripada grupi fakulteta 
		društveno-humanističkih nauka. Svojim planom i programom Fakultet pokriva međusobno povezane sociološke, političke, pravne, ekonomske, 
		etičke, psihološke, humanitarne, civilno-vojne i druge aspekte bezbednosti, ljudskih i socijalnih resursa, odbrane, civilne zaštite i 
		zaštite životne sredine.',305,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fakultet za specijalnu edukaciju i rehabilitaciju','Stari grad','Visokog Stevana','2','Defektološki fakultet je počeo sa radom 10. novembra 1975. godine. To je bio početak uspona jedne Profesije koja je u proteklih
		trideset godina tražila i pronalazila svoje puteve, poštovala svoju tradiciju, razvijala naučnu misao i primenjivala savremene tendencije
		u obrazovanju mladih stručnjaka. Istorijski posmatrano, defektologija je napravila nekoliko teških i džinovskih koraka u svom 
		razvoju, od srednjovekovne Srbije i brige o hendikepiranima na njenom tlu, do prolaska kroz svetske ratove i lečenje zaostalih rana, 
		koje su često ostavljale neizbrisivi hendikep kojem je trebalo znalački prići, do današnjih najsavremenijih pristupa u edukaciji 
		i rehabilitaciji.',306,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fakultet političkih nauka','Voždovac','Jove Ilića','165','Univerzitet u Beogradu – Fakultet političkih nauka (FPN) već 40 godina predstavlja vodeću instituciju u oblasti visokog obrazovanja
			koja je danas moderna i otvorena škola demokratije i demokratske političke kulture, sposobna da aktivno učestvuje i unapređuje 
			politički diskurs i odgovori na osnovne imperative političke egzistencije našeg društva i njegovog međunarodnog okruženja. Osnovan 
			je 1968. godine kao prva nastavno-naučna ustanova te vrste u tadašnjoj Jugoslaviji.',307,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fakultet sporta i fizičkog vaspitanja','Čukarica','Blagoja Parovića','156','Fakultet sporta i fizičkog vaspitanja Univerziteta u Beogradu je naučno-obrazovna institucija, koja u kontinuitetu, od 1946. godine, 
			obrazuje visokoškolske kadrove za fizičko vaspitanje u školama, sport i rekreaciju. Koreni Fakulteta sežu od 1938. godine kada je 
			formirana prva jednogodišnja škola za obrazovanje kadrova iz naše struke, odnosno do 1939. godine kada je počela sa radom Škola za 
			telesno vaspitanje u rangu više škole.',308,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Filozofski fakultet','Stari grad','Čika Ljubina','18-20','Filozofski fakultet je najstariji fakultet Univerziteta u Beogradu, čiji počeci sežu u 1838. godinu kada je aktom kneza Miloša u 
			Kragujevcu osnovan Licej. Danas je Filozofski fakultet moderna škola koja prati sve savremene tokove evropskog akademskog prostora.',309,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Filološki fakultet','Stari grad','Studentski trg','3','Filološke discipline imaju dugu tradiciju u srpskom školstvu, pa i u onom univerzitetskog tipa. Otuda se Filološki fakultet može 
			smatrati jednim od najstarijih na srpskim univerzitetima, mada je kao samostalan fakultet počeo da dela tek od 1960. godine, 
			kada se izdvojio iz Filozofskog fakulteta i formirao kao posebna naučna i nastavna visokoškolska ustanova.',310,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Biološki fakultet','Stari grad','Studentski trg','16','Dvadeseti vek je svedok prevazilaženja u biologiji ranijeg okvira iste kao deskriptivne i kvantitativne nauke. Otkrića molekularnih
			mehanizama ujedinila su u savremenim istraživačkim naporima biohemiju, citologiju, genetiku, fiziologiju i ostale biološke 
			discipline, te se dvadesetprvi vek označva kao vek biologije.
			Dostignuća čitavog niza različitih naučnih disciplina u okvirima biologije kao jedne kompleksne nauke, daju nov podsticaj u 
			proučavanju funkcionalne raznolikosti između pojedinih vrsta mikroorganizama, gljiva, biljaka i životina, a posebno čoveka.',311,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Geografski fakultet','Stari grad','Studentski trg','3/III','Geografski fakultet je državna visokoškolska ustanova čije su osnovne delatnosti visoko obrazovanje i naučni rad. Fakultet 
			izvodi studije na 3 stepena; osnovne, master i doktorske studije. Studije se odvijaju na srpskom jeziku, ali pruža se mogućnost 
			da se pojedini delovi studija kao i doktorske disertacije rade na bilo kom željenom jeziku.',312,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Matematički fakultet','Stari grad','Studentski trg','16','Geografski fakultet je državna visokoškolska ustanova čije su osnovne delatnosti visoko obrazovanje i naučni rad. Fakultet 
			izvodi studije na 3 stepena; osnovne, master i doktorske studije. Studije se odvijaju na srpskom jeziku, ali pruža se mogućnost 
			da se pojedini delovi studija kao i doktorske disertacije rade na bilo kom željenom jeziku.',313,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fakultet za fizičku hemiju','Stari grad','Studentski trg','12-16','Fizička hemija na Univerzitetu u Beogradu se predaje od 1903. godine. Delatnosti Fakulteta za fizičku hemiju danas su visoko obrazovanje 
			i naučnoistraživački rad od posebnog značaja za Republiku Srbiju, a koji su usklađeni sa međunarodnim, posebno evropskim obrazovnim i
			naučnim prostorom. Osnovna delatnost Fakulteta se obavlja u skladu sa zakonima o visokom obrazovanju i o naučnoistraživačkom radu,
			kao i sa statutima Univerziteta u Beogradu i Fakulteta.',314,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fizički fakultet','Stari grad','Studentski trg','12','Fizička hemija na Univerzitetu u Beogradu se predaje od 1903. godine. Delatnosti Fakulteta za fizičku hemiju danas su visoko obrazovanje 
			i naučnoistraživački rad od posebnog značaja za Republiku Srbiju, a koji su usklađeni sa međunarodnim, posebno evropskim obrazovnim i
