insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Hemijski fakultet','Stari grad','Studentski trg','12-16','Današnji Hemijski fakultet, kao institucija Beogradskog Univerziteta, vodi poreklo iz Liceja (1838-1863) i Velike škole (1863-1905). Licej je 
			u početku imao dva odeljenja: Filozofsko i Pravno. Godine 1853. na Liceju je formirano novo Prirodno-tehničko odeljenje na kojem se, 
			pored ostalih nauka, počela izučavati i hemija. Od osnivanja do danas na ovom fakultetu diplomiralo je 2898 studenata. Fakultet je 
			dao 97 specijalista i 345 magistara, kao i 204 doktora hemijskih nauka. Četiri Profesora Hemijskog fakulteta su članovi Srpske 4
			akademije nauka i umetnosti.',316,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Medicinski fakultet','Savski venac','dr Subotića','8','Studije medicine, koje se obavljaju na Medicinskom fakultetu Univerziteta u Beogradu, čine obuka lekara – obuka srednjoškolaca da postanu lekari,
			obuka lekara da postanu lekari specijalisti, obuka specijalista da postanu supspecijalisti, kontinuirana obuka svih profila 
			lekara radi upoznavanja sa poslednjim dostignućima medicinske nauke i struke, obuka istraživača u medicinskim naukama. Pomenuti
			posao se obavlja u skladu sa misijom Medicinskog fakulteta.',317,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Stomatološki fakultet','Savski venac','dr Subotića','8','Stomatološki fakultet Univerziteta u Beogradu vodeća je nastavna, naučna i stručna ustanova u oblasti stomatologije u zemlji. Zadatak Fakulteta je 
			da osposobi studente, buduće doktore stomatologije, da pacijentima pružaju usluge najvišeg kvaliteta i da kod njih razvije želju za
			neprekidnim usavršavanjem. Takođe zadatak je i da najtalentovanijim doktorima stomatologije omogući dalje usavršavanje i uputi ih u 
			tajne naučnoistraživačkog rada.',318,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fakultet veterinarske medicine','Savski venac','Bulevar oslobođenja','18','Fakultet veterinarske medicine Univerziteta u Beogradu smešten je u blizini centra grada u Bulevaru oslobođenja 18, nekoliko stotina metara od centralnog
			gradskog trga Slavije u smeru prema Autokomandi. U neposrednoj blizini fakulteta nalaze se Medicinski i Stomatološki fakultet. U 
			vreme kada je fakultet osnovan, dakle pre više od sedamdeset godina, nalazio se na rubu Beograda koji se počeo naglo razvijati.
			U poslednje vreme razmišlja se o dislokaciji fakulteta iz centra grada.',319,null,null);
insert into zgrada(naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Farmaceutski fakultet','Voždovac','Vojvode Stepe','450','Farmaceutski odsek pri Medicinskom fakultetu Beogradskog univerziteta osnovan je 24. oktobra 1939. U samostalnu visokoškolsku ustanovu Farmaceutski fakultet 
			prerasta 19. oktobra 1945. godine. Dugi niz godina nastava na fakultetu se odvijala u objektima Medicinskog fakulteta, a od septembra 1991. godine 
			Farmaceutski fakultet dobija svoju zgradu u ulici Vojvode Stepe 450. Od osnivanja pa do kraja 2007. godine na Farmaceutskom fakultetu 
			diplomiralo je 9.284 studenata, završilo specijalizaciju 1.788 kandidata (1.176 specijalizacija za potrebe zdravstva), odbranjeno je 
			286 magistarskih teza i 239 doktorskih disertacija.',320,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Arhitektonski fakultet','Palilula','Bulevar kralja Aleksandra','73/II','Farmaceutski odsek pri Medicinskom fakultetu Beogradskog univerziteta osnovan je 24. oktobra 1939. U samostalnu visokoškolsku ustanovu Farmaceutski fakultet 
			prerasta 19. oktobra 1945. godine. Dugi niz godina nastava na fakultetu se odvijala u objektima Medicinskog fakulteta, a od septembra 1991. godine 
			Farmaceutski fakultet dobija svoju zgradu u ulici Vojvode Stepe 450. Od osnivanja pa do kraja 2007. godine na Farmaceutskom fakultetu 
			diplomiralo je 9.284 studenata, završilo specijalizaciju 1.788 kandidata (1.176 specijalizacija za potrebe zdravstva), odbranjeno je 
			286 magistarskih teza i 239 doktorskih disertacija.',321,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Građevinski fakultet','Palilula','Bulevar kralja Aleksandra','73/I','Građevinski fakultet Univerziteta u Beogradu je najstarija i najviša obrazovna i naučna institucija u oblasti građevinarstva i geodezije u našoj zemlji.
			Fakultet ima dugu i bogatu tradiciju, počeci nastave iz oblasti građevinarstva na ovim prostorima vezuju se za 1846. godinu kad 
			je Knjaz Srpski Aleksandar Karađorđević svojim ukazom osnovao Indžinirsku školu u Beogradu. Stoga se ova godina nalazi i na grbu
			našeg fakulteta uz latinske reči studere, docere, aedificare koje simbolizuju delatnost fakulteta u oblasti nastave, nauke i 
			struke. Inženjeri potekli sa građevinskog fakulteta su ostvarili mnoge značajne uspehe u projektovanju i građenju svih vrsta 
			objekata u zemlji i inostranstvu, od kojih veliki broj spada u vrhunska graditeljska ostvarenja. Veliki broj bivših studenata 
			fakulteta radi u najprestižnijim svetskim građevinskim firmama odnosno na najpoznatijim univerzitetima u svetu.',322,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Elektrotehnički fakultet','Palilula','Bulevar kralja Aleksandra','73','Razvoj Elektrotehničkog fakulteta u bliskoj je vezi sa istorijom univerzitetskog obrazovanja u Srbiji. Prvo visokoškolsko predavanje iz oblasti 
			elektrotehnike održano je davne 1894. Profesor Stevan Marković bio je prvi predavač i osnivač Elektrotehničke katedre Inženjerskog 
			odeljenja Velike škole u Beogradu. Samo četiri godine kasnije, profesor Marković je osnovao i elektroinženjersku laboratoriju. Od 
			tada, elektrotehničke teme su se izučavale na Velikoj školi, a zatim i na beogradskom Univerzitetu koji je iz nje nastao 1905. Prve 
			diplome dodeljene su 1922 godine.',323,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Mašinski fakultet','Palilula','Kraljice Marij','16','Mašinski fakultet Univerziteta u Beogradu je najstarija i najveća visokoškolska i naučna ustanova u Srbiji i Jugoslaviji u oblasti mašinstva.',324,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Poljoprivredni fakultet','Zemun','Nemanjina','6','"Od skromnog početka sa nekoliko nastavnika, tri redovna, jednim vanrednim i tri honorarna, Poljoprivredni fakultet je danas jedna od vodećih 
			visokoškolskih ustanova u ovom delu Evrope, sa preko 500 zaposlenih i permanentim brojem od oko 4.500 studenata.',325,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Rudarsko-geološki fakultet','Palilula','Đušina','7','Rudarsko-geološki fakultet je obrazovno-naučna ustanova u sastavu Univerziteta u Beogradu. Obrazuje stručnjake na osnovnim, specijalističkim, 
			magistarskim i doktorskim studijama. Uz to obavlja inovacije znanja, stručnog obrazovanja i usavršavanja iz matičnih oblasti, 
			kao i naučnoistraživački rad. Do sada je na fakultetu diplomiralo 6437 studenata, magistriralo 590, specijaliziralo 17, a odbranilo 
			doktorsku disertaciju 448 kandidata.',326,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Saobraćajni fakultet','Voždovac','Vojvode Stepe','305','Prateći razvoj saobraćajne struke i nauke kao i potrebe privrede za diplomiranim saobraćajnim inženjerima, Saobraćajni fakultet je u proteklih 
			pedeset pet godina jedanaest puta vršio inovaciju nastavnih planova i programa. Svaki od njih odražavao je sve bogatije iskustvo 
			nastavnika i saradnika u nauci i obrazovanju, i koristio povratne informacije od saobraćajnih inženjera iz prakse kao i velikog
			broja korisnika usluga iz privrede, raznih državnih i društvenih organizacija i ustanova. Takođe, u ove planove i programe 
			ugrađivana su i iskustva sličnih škola u inostranstvu. Na osnovu ovog rada, Saobraćajni fakultet je matičan za sledeće naučne 
			oblasti u saobraćaju i transportu: planiranje, tehnologija, upravljanje-menadžment, organizacija, projektovanje, eksploatacija, 
			bezbednost, održavanje, ekonomija i logistika.',327,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Saobraćajni fakultet','Bor','Vojske Jugoslavije','12','Tehnički fakultet u Boru, jedini fakultet Univerziteta u Beogradu lociran van sedišta univerziteta, osnovan je 1961. godine kao odgovor na izražene 
			potrebe za visokoobrazovanim kadrom iz oblasti rudarstva i metalurgije u vreme dinamičnog razvoja Rudarsko-topioničarskog basena Bor.',328,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Tehnološko-metalurški fakultet','Palilula','Kernegijeva','4','Tehnološko-metalurški fakultet u Beogradu je najstarija visoko obrazovna i naučna institucija iz oblasti hemijskih tehnologija u našoj zemlji, 
			sa bogatom tradicijom, svetskim priznatim rezultatima i visokim ugledom. Fakultet obezbeđuje stručnjake za širok spektar kreativnih poslova iz različitih oblasti.',329,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Fakultet organizacionih nauka','Voždovac','Jove Ilića','154','Organizacione nauke, koje doživljavaju puni razvoj u drugoj polovini 20. veka, dale su značajan doprinos razvoju privrede i društva u celini. Intenzivni razvoj 
			organizacionih nauka definisanih u oblastima menadžmenta i informacionih sistema i tehnologija uslovio je rast efikasnosti i 
			napredovanje poslovnih sistema. To je podstaklo nastanak velikog broja institucija visokog obrazovanja u ovoj oblasti. U bivšoj 
			Jugoslaviji, prve ideje za formalnim obrazovanjem u oblasti organizaconih nauka pojavile su se pre više od pedeset godina. Kao plod 
			takvih inicijativa i evidentne potrebe za obrazovanjem stručnjaka koji će voditi preduzeća i biti nosioci razvoja i promena, 
			1969. godine je doneta odluka o osnivanju Fakulteta organizacionih nauka u Beogradu. U januaru 1971. godine odlukom Saveta 
			Univerziteta u Beogradu, Fakultet organizacionih nauka je postao članica Univerziteta.',330,null,null);
insert into zgrada (naziv, opstina, ulica, broj, opis, fakultet_id, restoran_id, dom_id) values('Šumarski fakultet','Čukarica','Kneza Višeslava','1','Šumarski fakultet Univerziteta u Beogradu je najstarija i najviša visokoškolska i naučna institucija u oblasti šumarstva, prerade drveta, pejzažne arhitekture i 
			hortikulture i ekološkog inženjeringa u zaštiti zemljišnih i vodnih resursa u Srbiji.',331,null,null);